package com.anhbq.sketch.service;

import com.anhbq.sketch.model.Sketch;
import com.anhbq.sketch.model.SketchRest;
import com.anhbq.sketch.repository.SketchRepository;
import lombok.AllArgsConstructor;
import org.apache.tomcat.util.codec.binary.Base64;
import org.apache.tomcat.util.codec.binary.StringUtils;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.PageRequest;
import org.springframework.data.domain.Pageable;
import org.springframework.data.domain.Sort;
import org.springframework.stereotype.Service;
import java.util.List;
import java.util.stream.Collectors;

@Service
@AllArgsConstructor
public class SketchService {

    private static final String IMAGE_URL = "data:image/png;base64,";

    private final SketchRepository sketchRepository;

    public SketchRest saveSketch(SketchRest request) {
        Sketch sketch = new Sketch(
                request.getId(),
                request.getName(),
                this.convertURLStringToByte((request.getImageURL() == null) ? IMAGE_URL : request.getImageURL()),
                request.getBackground(),
                request.getWidth(),
                request.getHeight()
        );
        return this.convertSketchToSketchRest(this.sketchRepository.save(sketch));
    }

    public List<SketchRest> getAllSketch() {
        return this.sketchRepository.findAll().stream().map(
                this::convertSketchToSketchRest
        ).collect(Collectors.toList());
    }

    public Page<SketchRest> getAllSketch(Integer page, Integer size) {
        Pageable pageable = PageRequest.of(page, size, Sort.by("id"));
        return this.sketchRepository.findAll(pageable).map(
                this::convertSketchToSketchRest
        );
    }

    public List<SketchRest> findSketchesContaining(String name) {
        return this.sketchRepository.findByNameContaining(name).stream().map(
                this::convertSketchToSketchRest
        ).collect(Collectors.toList());
    }

    public Page<SketchRest> findSketchesContaining(String name, Integer page, Integer size) {
        Pageable pageable = PageRequest.of(page, size, Sort.by("id"));
        return this.sketchRepository.findByNameContaining(name, pageable).map(
                this::convertSketchToSketchRest
        );
    }

    public SketchRest getSketchById(Long id) {
        return this.convertSketchToSketchRest(this.handleFindingSketchByID(id));
    }

    public SketchRest updateSketch(SketchRest request) {
        Sketch result = this.handleFindingSketchByID(request.getId());
        result.setName(request.getName());
        result.setImageURL(this.convertURLStringToByte((request.getImageURL() == null) ? IMAGE_URL : request.getImageURL()));
        result.setBackground(request.getBackground());
        result.setWidth(request.getWidth());
        result.setHeight(request.getHeight());
        return this.convertSketchToSketchRest(this.sketchRepository.save(result));
    }

    public SketchRest updateSketchName(Long id, String name) {
        Sketch sketch = this.handleFindingSketchByID(id);
        sketch.setName(name);
        return this.convertSketchToSketchRest(this.sketchRepository.save(sketch));
    }

    public SketchRest updateImageURL(Long id, String imageURL) {
        Sketch sketch = this.handleFindingSketchByID(id);
        sketch.setImageURL(this.convertURLStringToByte(imageURL));
        return this.convertSketchToSketchRest(this.sketchRepository.save(sketch));
    }

    public SketchRest updateSketchBackground(Long id, String background) {
        if (background.matches("#\\d{6}")) {
            throw new IllegalStateException("Background Input only allows format of Hex Color");
        }
        Sketch sketch = this.handleFindingSketchByID(id);
        sketch.setBackground(background);
        return this.convertSketchToSketchRest(this.sketchRepository.save(sketch));
    }

    public SketchRest updateSketchSize(Long id, Integer width, Integer height) {
        if (width < 0 || height < 0) {
            throw new IllegalStateException("Size Inputs do not allow negative numbers");
        }
        Sketch sketch = this.handleFindingSketchByID(id);
        sketch.setWidth(width);
        sketch.setHeight(height);
        return this.convertSketchToSketchRest(this.sketchRepository.save(sketch));
    }

    public SketchRest deleteSketch(Long id) {
        Sketch sketch = this.handleFindingSketchByID(id);
        this.sketchRepository.delete(sketch);
        return this.convertSketchToSketchRest(sketch);
    }

    private Sketch handleFindingSketchByID(Long id) {
        return this.sketchRepository.findById(id).orElseThrow(
                () -> new IllegalStateException(String.format("Sketch %d not found", id))
        );
    }

    private byte[] convertURLStringToByte(String stringUrl) {
        String encodingPrefix = "base64,";
        int contentStartIndex = stringUrl.indexOf(encodingPrefix) + encodingPrefix.length();
        byte[] imageData = Base64.decodeBase64(stringUrl.substring(contentStartIndex));
        return imageData;
    }

    private SketchRest convertSketchToSketchRest(Sketch sketch) {
        return new SketchRest(
                sketch.getId(),
                sketch.getName(),
                sketch.getBackground(),
                sketch.getWidth(),
                sketch.getHeight(),
                this.convertByteArrayToStringURL(sketch.getImageURL())
        );
    }

    private String convertByteArrayToStringURL(byte[] bytes) {
        StringBuilder sb = new StringBuilder();
        sb.append("data:image/png;base64,");
        sb.append(StringUtils.newStringUtf8(Base64.encodeBase64(bytes, false)));
        return sb.toString();
    }
}
